use crate::error::Error;
use bstr::ByteSlice;
use std::io::Read;

const LOAD_FILE: &str = "/proc/stat";

#[derive(Debug, Default)]
pub struct Load {
    pub busy: f64,
    pub idle: f64,
}

pub fn read_cpu_load() -> Result<Load, Error> {
    let mut slice = [0; 128];
    let mut file = std::fs::File::open(LOAD_FILE)?;
    file.read_exact(&mut slice)?;
    parse_raw(&slice)
}

pub fn parse_raw(content: &[u8]) -> Result<Load, Error> {
    let mut prev_ind = 0;
    let mut it = 0;
    let mut busy = 0;
    let mut idle = 0;
    while let Some(space_ind) = content[prev_ind..].find_byte(b' ') {
        if prev_ind == 0 {
            prev_ind = space_ind;
            continue;
        }
        prev_ind += if space_ind == 0 {
            1
        } else {
            let target = &content[prev_ind..prev_ind + space_ind];
            if it == 3 {
                idle = atoi::atoi::<u64>(target).ok_or(Error::ProcStatParseError)?;
            } else if it > 7 {
                break;
            } else {
                busy += atoi::atoi::<u64>(target).ok_or(Error::ProcStatParseError)?;
            }
            it += 1;
            space_ind
        };
    }
    Ok(Load {
        busy: busy as f64,
        idle: idle as f64,
    })
}

